import React, { Component } from 'react';


class CategoriesQ extends Component {

    constructor(){
        super()
        this.state = {
            
        }
    }

    componentDidMount() {
       /* fetch('https://undergames.herokuapp.com/api/products/list') */
       fetch('http://localhost:3005/api/products/list')
        .then(respuesta => {
            return respuesta.json()
        })
        .then(totalesCat => {
            this.setState({totalCategories: Object.keys(totalesCat.data.countByCategory).length
                        
                    })
                    
            })
        
        .catch(error => console.log(error))
    }

    render() {
        return (
            <React.Fragment>
                totCat: {this.state.totalCategories}
              
                </React.Fragment>
        )
       }
    

}


export default CategoriesQ;
