import React,{ useState,useEffect } from "react";

function GameList(){
    

    const [game, setGame] = useState(["Loading.."])
    
    
    const fetchDataGameList = () => {    
    const response = fetch('http://localhost:3005/api/products/list');
    const gameData = response.json();
    setGame(gameData.data.products);
    };

    useEffect(() => {
        fetchDataGameList();
        }, []);
    
   return(
            <React.Fragment>
                {/*<!-- PRODUCTS LIST -->*/}
                   <h1 className="h3 mb-2 text-gray-800">All the Games in the Database</h1>
                        
                        <div className="card shadow mb-4">
                            <div className="card-body">
                                <div className="table-responsive">
                                    <table className="table table-bordered" id="dataTable" width="100%" cellSpacing="0">
                                        <thead>
                                            <tr>
                                                <th>Id</th>
                                                <th>Titulo</th>
                                                <th>Categoria</th>
                                                <th>Grupo</th>
                                                <th>Creador</th>
                                            </tr>
                                        </thead>
                                        <tfoot>
                                            <tr>
                                                <th>Id</th>
                                                <th>Titulo</th>
                                                <th>Categoria</th>
                                                <th>Grupo</th>
                                                <th>Creador</th>
                                            </tr>
                                        </tfoot>
                
                                        <tbody>
                                        {game.map(row => {
                                        return (
                                        <tr key={row.id}>
                                            <td>{row.id}</td>
                                            <td>{row.name}</td>
                                            <td>{row.category}</td>
                                            <td>Grupo</td>
                                            <td>Creador</td>
                                            {/*<!-- <td>{row.id_category}</td>-->*/}
                                            {/*<!-- <td>{row.id_game_group}</td>-->*/}
                                            {/*<!-- <td>{row.id_creators}</td>-->*/}
                                        </tr>
                                    );
                            })}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>            
            </React.Fragment>
        )
    }

    export default GameList;
