import React,{ useState,useEffect } from "react";

function LastGameInDb(){
    // levanto todos los  juegos, para ver el último

    const [product, setProduct] = useState(["Loading.."])
    const fetchData = async () => {
    const response = await fetch('http://localhost:3005/api/products/list');
    const data = await response.json();
    setProduct(data);
    };
    useEffect(() => {
        fetchData();
        }, []);

    let lastGameArr = setProduct[setProduct.length - 1];

    console.log(lastGameArr)

        /*let lastGameId = lastGameArr.id_game

        console.log('lastGameId: ', lastGameId)


        // levanto el detalle del ultimo  juego

        const [productDetail, setProductDetail] = useState(["Loading.."])
        async function fetchDetailLastGame() {       
            const respuestaDetail = await fetch('http://localhost:3005/api/products/detail/:'+ {lastGameId});        
            const productsDetailArray = await respuestaDetail.json();            
            setProductDetail(productsDetailArray.productDetail)
        }
        useEffect(()=>{
            fetchDetailLastGame()
        },[]);

        let lastGame = productDetail[lastGameId]

        console.log(lastGame)

*/

    return(
        <React.Fragment>
            <div className="col-lg-6 mb-4">
            <div className="card shadow mb-4">
                <div className="card-header py-3">
                    <h5 className="m-0 font-weight-bold text-gray-800">Last Game in Data Base</h5>
                </div>
                <div className="card-body">
                    <div className="text-center">
                        <img className="img-fluid px-3 px-sm-4 mt-3 mb-4" style={{width: 40 +'rem'}} src={"http://localhost:3005/images/games/" + lastGame.img_1}/>
                    </div>
                    <p>{lastGame.short_descr}</p>
                    <a className="btn btn-danger" target="_blank" rel="nofollow" href={"http://localhost:3001/products/detail/:"+ lastGame.id_game}>View Detail</a>
                </div>
            </div>
            </div>
        </React.Fragment>
    )
}







export default LastGameInDb;
